import React from "react";
import { Link } from "react-router-dom";
import { Button } from 'semantic-ui-react'


const ContactCard = (props) => {
  const { id, name, picture } = props.contact;
  return (
    <center>
    <div className="card">
    <center><img className="img" src={picture} alt="device" height="200px" width="200px" /></center>
    <ul class="list-group list-group-flush">
    <li class="list-group-item"><b>Name:</b> <br/> {name}</li>
    </ul>
    <div className="card-body">

<center><i
className="trash alternate outline icon"
style={{ color: "red", marginTop: "10px"}}
onClick={() => props.clickHander(id)}
></i></center>
<Link to={{ pathname: `/edit`, state: { contact: props.device } }}>
<center><i
className="edit alternate outline icon"
style={{ color: "blue", marginTop: "10px" }}
></i> </center>
</Link>

<br/>
<Link to={{ pathname: `/Art/${id}`, state: { contact: props.device } }}>
<Button color='blue'>View Details</Button>
</Link>
</div>
</div>
<br/>
</center>
);
};

