export default function Clock(props) {
  const [date, setDate] = useState(new Date());

 useEffect(() => {
  var timer = setInterval( () => setDate(new Date()), 1000 );

  return function cleanup() {
      clearInterval(timer);
    };
 });

   return (
      <div>


        <h2>   <img className="img" src="https://img.icons8.com/android/24/000000/clock.png"/>Time: {date.toLocaleTimeString()}.</h2>
      </div>
    );}
