import React from "react";
import { Link } from "react-router-dom";


const ContactCard = (props) => {
  const { id, name, picture } = props.contact;
  return (


    <div className="item">

      {/* <img src="cinqueterre.jpg" class="rounded-circle" alt="Cinque Terre" width="304" height="236"/>  */}

      <img class="rounded-circle" src="https://f.top4top.io/p_20007399o1.png"  alt="Cinque Terre" width="500px" height="250px"/>  

   <center>
                 <div className="card">
                 <center><img className="img" src={image} alt="device" height="200px" width="200px" /></center>
                 <ul class="list-group list-group-flush">
                 <li class="list-group-item"><b>ID:</b> {id}</li>
                 <li class="list-group-item"><b>Name:</b> <br/> {name}</li>
                 <li class="list-group-item"><b>Price:</b> {price} SAR</li>
                 </ul>
                 <div className="card-body">

      <center><i
        className="trash alternate outline icon"
        style={{ color: "red", marginTop: "10px"}}
        onClick={() => props.clickHander(id)}
      ></i></center>
      <Link to={{ pathname: `/edit`, state: { device: props.device } }}>
      <center><i
          className="edit alternate outline icon"
          style={{ color: "blue", marginTop: "10px" }}
        ></i> </center>
      </Link>

      <br/>
      <Link to={{ pathname: `/device/${id}`, state: { device: props.device } }}>
           <Button color='blue'>View Details</Button>
        </Link>
        </div>
    </div>
    <br/>
    </center>
  );
};


      <div className="content">
        <Link
          to={{ pathname: `/Art/${id}`, state: { contact: props.contact } }}
        >
          <div className="header">{name}</div>
          <img src={picture} width="350" height="350"/>
        </Link>
      </div>
      <i
        className="trash icon"
        style={{ color: "brown", marginTop: "7px", marginLeft: "10px" }}
        onClick={() => props.clickHander(id)}
      ></i>
      <Link to={{ pathname: `/edit`, state: { contact: props.contact } }}>
        <i
          className="edit icon"
          style={{ color: "brown", marginTop: "7px" }}
        ></i>
      </Link>
    </div>
  );
};

export default ContactCard;
