import React from "react";
import { Link } from "react-router-dom";
import user from "../images/user.png";


const ContactCard = (props) => {
  const { id, name, picture } = props.contact;
  return (

    <div className="item">

      <img src="https://img.freepik.com/free-vector/coffee-love-foam-with-beans-cartoon-icon-illustration_138676-2575.jpg?size=338&ext=jpg" class="rounded-circle" alt="Cinque Terre" width="304" height="236"/> 


      <div className="content">
        <Link
          to={{ pathname: `/contact/${id}`, state: { contact: props.contact } }}
        >
          <div className="header">{name}</div>
          <img src={picture} width="350" height="350"/>
        </Link>
      </div>
      <i
        className="trash icon"
        style={{ color: "brown", marginTop: "7px", marginLeft: "10px" }}
        onClick={() => props.clickHander(id)}
      ></i>
      <Link to={{ pathname: `/edit`, state: { contact: props.contact } }}>
        <i
          className="edit icon"
          style={{ color: "brown", marginTop: "7px" }}
        ></i>
      </Link>
    </div>
  );
};

export default ContactCard;
